<div class="row mt-3">
    <div class="col">
        <div class="form-group error-danger error-danger-name">
            <label>Name</label>
            <input class="form-control" placeholder="Name" name="name" required>
            <span class="text-danger d-none error-span error-name"></span>
        </div>
    </div>
</div>
<div class="row">
    <div class="col">
        <div class="form-group error-danger error-danger-content">
            <label>Content</label>
            <textarea class="form-control summernote" name="content" required></textarea>
            <span class="text-danger d-none error-span error-content"></span>
        </div>
    </div>
</div>
